//     {
//         id: 8,
//         title: "Two And a Half Men",
//         image: TwoMen,
//         rate: 7,
//     },
// ];
const Home = () => {
    const [movies, setMovies] = useState([]);
    const [inputValue, setInputValue] = useState("Any");

    const getMovies = async () => {
        try {
            const response = await fetch(
                `https://cogitize-practice-suggest.onrender.com/movie/list`
            );
            const data = await response.json();

            setMovies(data);
        } catch (error) {
            console.log("Catch error :", error);
        }
    };

    const getMoviesForQuery = async (value) => {
        try {
            const response = await fetch(
                `https://cogitize-practice-suggest.onrender.com/movie/list?category=${value}`
            );
            const data = await response.json();

            setMovies(data);
        } catch (error) {
            console.log("Catch error :", error);
        }
    };

    useEffect(() => {
        getMovies();
    }, []);

    const handleInput = (e) => {
        const value = e.target.id;
        setInputValue(value);

        getMoviesForQuery(value);
    };



    return (
        <div className={style.container}>
            <div className={style.content}>
                <Title />
                <div className={style.input_checker}>
                    <Input onChange={handleInput} value={inputValue}/>
                </div>
                <div className={style.list_info}>
                    Any <span>(120)</span>
                </div>
                <ul className={style.list}>
                    {movies.map((item) => {
                        return (
                            <li key={item._id}>
                                <Link to={`details/${item._id}`}>
                                    <Card data={item} />
                                </Link>
                            </li>
                        );
                    })}
                </ul>
                <div className={style.bottom}>
                    <div>Didin’t find the one you looking for?</div>
                    <button>Suggest more</button>
                </div>

            </div>
        </div>
    );
}

export default Home;
